import math#Verilerv_0= float(input("Başlangıç hızı:"))θ= float(input("Atış açısı:"))g= 9.81dt= 0.01 #radyan dönüşümüθ_radyan= θ*(math.pi/180)#hızv_x= v_0*math.cos(θ_radyan)v_y0= v_0*math.sin(θ_radyan)#Başlangıç değerlerit=0x=0y=0max_y=0#While döngüsüwhile y>=0:    x= v_x*t    v_y= v_y0-g*t    y= v_y0*t-0.5*g*t**2        if y>max_y:        max_y=y            t+= dt    #Sonuçprint(f"Uçuş süresi: {t:.2f}saniye")print(f"Maksimum yükseklik: {max_y:.2f} metre")print(f"Menzil(x): {x:.2f} metre")